<?php

namespace Tests\Feature;

use Illuminate\Foundation\Testing\TestResponse;
use Illuminate\Http\UploadedFile;
use Tests\TestCase;
use Illuminate\Support\Facades\Storage;

class UploadImageTest extends TestCase
{

    const UPLOAD_URL = '/api/upload';

    /**
     * Тест загрузки одиночного изоборажения через формдату
     */
    public function testCanUploadSingleFormData()
    {
        Storage::fake('avatars');
        $file = UploadedFile::fake()->image('avatar.jpg');
        $response = $this->json('POST', self::UPLOAD_URL, [
            'image' => $file
        ]);
        $this->checkImagesExists($response);
    }

    /**
     * Тест загрузки мультизагрузки изображений через формдату
     */
    public function testCanUploadManyFormData()
    {
        Storage::fake('avatars');
        $file = UploadedFile::fake()->image('avatar.jpg');
        $file2 = UploadedFile::fake()->image('avatar1.jpg');
        $file3 = UploadedFile::fake()->image('avatar2.jpg');
        $response = $this->json('POST', self::UPLOAD_URL, [
            'image' => [$file, $file2, $file3],
        ]);
        $this->checkImagesExists($response);
    }

    /**
     * Тест загрузки одиночного изображений через URL
     */
    public function testCanUploadSingleUrl()
    {
        $url = 'https://insurancemarket.ng/images/thumbnails/649/220/detailed/3/26e178f.png';
        $response = $this->json('POST', self::UPLOAD_URL, [
            'urls' => $url
        ]);
        $this->checkImagesExists($response);
    }

    /**
     * Тест загрузки мультизагрузки через URL
     */
    public function testCanUploadManyUrls()
    {
        $urls = [
            'https://insurancemarket.ng/images/thumbnails/649/220/detailed/3/26e178f.png',
            'http://mediad.publicbroadcasting.net/p/wunc/files/styles/x_large/public/201705/standardized_test.jpg',
            'https://d11wkw82a69pyn.cloudfront.net/siteassets/images/test-automation/canvas/1440x950_canvas_test_automation.jpg'

        ];
        $response = $this->json('POST', self::UPLOAD_URL, [
            'urls' => $urls
        ]);
        $this->checkImagesExists($response);
    }

    /**
     * Тест загрузки одиночного изображения через base64
     */
    public function testCanUploadSingleBase64()
    {
        $url = 'data:image/jpeg;base64,';
        $response = $this->json('POST', self::UPLOAD_URL, [
            'base64' => $url
        ]);

        $this->checkImagesExists($response);
    }

    /**
     * Тест мультизагрузки через base64
     */
    public function testCanUploadManyBase64()
    {
        $urls = ['data:image/jpeg;base64,',
            'data:image/jpeg;base64,',
            'data:image/jpeg;base64,'
        ];

        $response = $this->json('POST', self::UPLOAD_URL, [
            'base64' => $urls
        ]);

        $this->checkImagesExists($response);
    }

    /**
     * Тест проверяет ответ при не правильном запросе
     */
    public function testEmptyRequest()
    {
        $response = $this->json('POST', self::UPLOAD_URL, []);
        $response->assertStatus(401);
    }
    
    /**
     * Проверка существования изображения в загруженных файлах в Storage
     *
     * @param TestResponse $response
     */
    private function checkImagesExists($response)
    {
        $images = json_decode($response->getContent());
        foreach ($images as $image) {
            Storage::disk('previews')->assertExists($image);
        }
        Storage::disk('previews')->assertMissing('missing.jpg');
    }

}
